import React from "react";

export default function Cart(props) {
  return (
    <div className="cart text-darkgray w-1/4">
      <div className="flex bg-[#9C0010] px-2 py-3 text-white font-bold">
        Your Order
      </div>
      <div className="flex px-2 py-3 justify-evenly items-center">
        <img
          src="https://www.mk1642.com/getattachment/f2af6d3f-b7aa-40bd-8de1-e2ee113575de/4131.aspx"
          width={70}
        />
        <h1>ต้มยำ</h1>
        <div className="flex items-center">
          <button className="border px-2 mx-3 rounded-full" onClick={minus}>
            -
          </button>
          <p id="quantity">1</p>
          <button className="border px-2 mx-3 rounded-full" onClick={plus}>
            +
          </button>
        </div>
        <p>218 .-</p>
      </div>
      <div className="flex px-2 py-3 justify-evenly items-center">
        <h1>Total</h1>
        <p>218 .-</p>
      </div>
      {/*  create checkout order button */}
      <div className="flex px-2 py-3 justify-center items-center">
        <button
          className="flex bg-red text-white px-4 py-2 rounded-md hover:shadow-md items-center justify-center"
          onClick={checkout}
        >
          ✓ Checkout
        </button>
      </div>
    </div>
  );
}

//create checkout funcion with api
function checkout() {
  console.log("checkout");
  // console.log("cart");
}
function plus() {
  var quantity = parseInt(document.getElementById("quantity").innerHTML);
  quantity += 1;
  document.getElementById("quantity").innerHTML = quantity;
}

function minus() {
  var quantity = parseInt(document.getElementById("quantity").innerHTML);
  quantity -= 1;
  document.getElementById("quantity").innerHTML = quantity <= 1 ? 1 : quantity;
}
