const filterlist = [
  {
    title: "จานเดี่ยว",
    content: [
      { id: 1, name: "ข้าว" },
      { id: 2, name: "บะหมี่" },
      { id: 3, name: "สุกี้ชาม" },
      { id: 4, name: "เกี๊ยว" },
    ],
  },
];

export default function FilterBar(props) {
  const Filter = filterlist[0].content.map((filter, index) => (
    <div key={index} className="flex mx-3">
      <input
        type="checkbox"
        checked={filter.id === props.filter}
        onChange={props.onFilterChange}
      />
      <h1>{filter.name}</h1>
    </div>
  ));

  return (
    <div className="flex w-full bg-gray justify-center items-center py-5">
      <h1>{filterlist[0].name}</h1>
      {Filter}
    </div>
  );
}
